const data = $input.first().json;

// Формируем таблицу для email
let tableRows = data.items.map((item, idx) => {
  return `<tr>
    <td style="border:1px solid #ddd;padding:8px;text-align:center;">${idx + 1}</td>
    <td style="border:1px solid #ddd;padding:8px;">${item.metal_type || item.metal_name || ''}</td>
    <td style="border:1px solid #ddd;padding:8px;text-align:center;">${item.size || ''}</td>
    <td style="border:1px solid #ddd;padding:8px;text-align:center;">${item.steel_grade || ''}</td>
    <td style="border:1px solid #ddd;padding:8px;text-align:center;">${item.weight ? Number(item.weight).toFixed(3) : ''}</td>
    <td style="border:1px solid #ddd;padding:8px;text-align:center;">${item.pieces || ''}</td>
  </tr>`;
}).join('');

// Тема письма с срочностью
const emailSubject = `Заказ ${data.requestId} | МеталлСтройГрупп`;

// Блок комментария менеджера (если есть)
const managerCommentBlock = data.managerComment ? `
  <div style="background-color: #f0f9ff; border-left: 4px solid #3b82f6; padding: 12px 15px; margin: 15px 0;">
    <p style="margin: 0; color: #1e40af;"><strong>💬 Сообщение от менеджера:</strong></p>
    <p style="margin: 8px 0 0 0; color: #333;">${data.managerComment}</p>
  </div>
` : '';

// Тело письма
const emailBody = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
</head>
<body style="font-family: Arial, sans-serif; font-size: 14px; color: #333;">
  <p>Добрый день!</p>
  ${managerCommentBlock}
  <p>Просим предоставить цены на следующие позиции:</p>

  <p><strong>Заявка №${data.requestId}</strong></p>

  <table style="border-collapse: collapse; width: 100%; margin: 15px 0;">
    <thead>
      <tr style="background-color: #f5f5f5;">
        <th style="border:1px solid #ddd;padding:8px;">№</th>
        <th style="border:1px solid #ddd;padding:8px;">Наименование</th>
        <th style="border:1px solid #ddd;padding:8px;">Размер</th>
        <th style="border:1px solid #ddd;padding:8px;">Марка стали</th>
        <th style="border:1px solid #ddd;padding:8px;">Вес, т</th>
        <th style="border:1px solid #ddd;padding:8px;">Кол-во</th>
      </tr>
    </thead>
    <tbody>
      ${tableRows}
      <tr style="font-weight: bold; background-color: #f0f0f0;">
        <td colspan="4" style="border:1px solid #ddd;padding:8px;">ИТОГО:</td>
        <td style="border:1px solid #ddd;padding:8px;text-align:center;">${data.totalWeight} т</td>
        <td style="border:1px solid #ddd;padding:8px;"></td>
      </tr>
    </tbody>
  </table>

  <div style="background-color: #fff3cd; border: 1px solid #ffc107; padding: 15px; margin: 20px 0; border-radius: 5px;">
    <p style="margin: 0 0 10px 0; font-weight: bold; color: #856404;">⚡ ВАЖНО: Сбор цен заканчивается через 30 минут!</p>
    <p style="margin: 0; color: #856404;">Если отвечаете позже — ваша цена может быть не учтена.</p>
  </div>

  <div style="background-color: #e7f3ff; border: 1px solid #2196F3; padding: 15px; margin: 20px 0; border-radius: 5px;">
    <p style="margin: 0 0 10px 0; font-weight: bold; color: #1565c0;">📋 Как ответить:</p>
    <ol style="margin: 0; padding-left: 20px; color: #1565c0;">
      <li>Скачайте прикреплённый Excel-файл</li>
      <li>Заполните колонку <strong>«Цена»</strong></li>
      <li>При необходимости заполните колонку <strong>«Примечания»</strong> — если нет полного объёма, сроки отличаются или есть другие нюансы</li>
      <li>Отправьте файл ответным письмом</li>
    </ol>
  </div>

  <p>Спасибо за оперативность!</p>

  <p style="margin-top: 30px; padding-top: 15px; border-top: 1px solid #ddd; color: #666;">
    С уважением,<br>
    <strong>МеталлСтройГрупп РФ</strong><br>
    Отдел закупок<br>
    zakup@msgnn.ru
  </p>
</body>
</html>
`;

// Готовим результат
const result = {
  json: {
    ...data,
    emailSubject,
    emailBody
  }
};

// Если есть Excel - добавляем как binary вложение
if (data.excelBase64 && data.excelBase64.length > 0) {
  result.binary = {
    attachment: {
      data: data.excelBase64,
      mimeType: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
      fileName: data.excelFilename || 'Запрос.xlsx'
    }
  };
  console.log('📎 Вложение подготовлено:', data.excelFilename);
} else {
  console.log('⚠️ Excel не найден в данных!');
}

return [result];

// Берём данные из ПРАВИЛЬНОЙ ноды
const data = $('Code: Get Suppliers by Category').item.json;

// Время сбора (через 45 минут)
const collectAt = new Date(Date.now() + 45 * 60 * 1000);
const collectAtFormatted = collectAt.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });

return [{
  json: {
    success: true,
    request_id: data.requestId,
    category: data.category,
    category_name: data.categoryName,
    suppliers_count: data.supplierCount,
    collect_at: collectAt.toISOString(),
    collect_at_formatted: collectAtFormatted,
    message: `Запрос отправлен ${data.supplierCount} поставщикам. Сводка будет готова в ${collectAtFormatted}`
  }
}];
